pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IConfig.sol";
import "../IUtilityToken.sol";
import "../feerouter/IFeeRouter.sol";
import "../curve/IPoolFactory.sol";
import "../curve/IPool.sol";

contract Config is IConfig, Initializable, OwnableUpgradeable, UUPSUpgradeable {
    // accept stable coins
    mapping(address => StableCoinEnabled) private _stableCoinEnabled;
    address[] private _stableCoins;

    // sell token
    IUtilityToken private _utilityToken;

    // pr token
    IUtilityToken private _prToken;

    // stable coin
    IUtilityToken private _stableCoinToken;

    IUtilityToken private _govToken;

    IFeeRouter private _feerouter;

    // mint LNCO fee
    uint256 public marketBuyfee;

    // burn LNCO fee
    uint256 public marketSellfee;

    // max once exchange amount
    // The maximum number of transactions that can be made in a single transaction
    // unthinkable bugs caused by large transaction amounts
    uint256 private _maxOnceExchangeAmount;

    address private _utilityStakeAddress;
    address private _liquidityStakeAddress;
    address private _vammAddress;
    address private _curve2CRVXPoolAddress;
    address private _minter;

    mapping(address => bool) private _operators;

    function initialize(
        IFeeRouter feerouter_,
        IUtilityToken utilityToken_,
        IUtilityToken govToken_,
        IUtilityToken prToken_,
        IUtilityToken stableCoinToken_,
        uint256 maxOnceExchangeAmount_,
        uint256 initMarketBuyfee_,
        uint256 initMarketSellfee_
    ) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        _transferOwnership(tx.origin);

        _feerouter = feerouter_;
        _maxOnceExchangeAmount = maxOnceExchangeAmount_;

        _utilityToken = utilityToken_;
        _prToken = prToken_;
        _stableCoinToken = stableCoinToken_;
        _govToken = govToken_;

        marketBuyfee = initMarketBuyfee_;
        marketSellfee = initMarketSellfee_;

        _deployCurve2CRVPool();
    }

    function getInitializedVersion() external view returns (uint8) {
        return _getInitializedVersion();
    }

    function _deployCurve2CRVPool() internal {
        _curve2CRVXPoolAddress = IPoolFactory(
            address(0xb17b674D9c5CB2e441F8e196a2f048A81355d031)
        ).deploy_metapool(
                address(0x7f90122BF0700F9E7e1F688fe926940E8839F353),
                "LNUSD",
                "LNUSD",
                address(_stableCoinToken),
                100000,
                4000000,
                1
            );
    }

    function vammBuyFees(uint256 totalPrice) external view returns (uint256) {
        if (marketBuyfee == 0) {
            return 0;
        }
        return (totalPrice * marketBuyfee) / 10000000000;
    }

    function vammSellFees(uint256 totalPrice) external view returns (uint256) {
        if (marketSellfee == 0) {
            return 0;
        }
        return (totalPrice * marketSellfee) / 10000000000;
    }

    function updateFees(uint256 buyFee, uint256 sellFee) external onlyOwner {
        marketBuyfee = buyFee;
        marketSellfee = sellFee;
    }

    function updateStableCoin(
        address stableCoin_,
        bool canBuy,
        bool canSell,
        address gauge_,
        bool isMetaGauge
    ) external onlyOwner {
        StableCoinEnabled storage stableCoinEnabled = _stableCoinEnabled[
            stableCoin_
        ];

        if (!stableCoinEnabled.exists) {
            _stableCoins.push(stableCoin_);
            stableCoinEnabled.exists = true;
        }

        stableCoinEnabled.buyEnabled = canBuy;
        stableCoinEnabled.sellEnabled = canSell;
        stableCoinEnabled.gauge = gauge_;
        stableCoinEnabled.isMetaGauge = isMetaGauge;
    }

    function updateMaxOnceExchangeAmount(
        uint256 maxOnceExchangeAmount_
    ) external onlyOwner {
        _maxOnceExchangeAmount = maxOnceExchangeAmount_;
    }

    function getVAMMStableCoin(
        address stablecoin
    ) external view returns (StableCoinEnabled memory) {
        return _stableCoinEnabled[stablecoin];
    }

    function getMaxOnceExchangeAmount() external view returns (uint256) {
        return _maxOnceExchangeAmount;
    }

    function getFeeRouter() external view returns (IFeeRouter) {
        return _feerouter;
    }

    function getUtilityToken() external view returns (IUtilityToken) {
        return _utilityToken;
    }

    function getPRToken() external view returns (IUtilityToken) {
        return _prToken;
    }

    function getGovToken() external view returns (IUtilityToken) {
        return _govToken;
    }

    function getStableCoinToken() external view returns (IUtilityToken) {
        return _stableCoinToken;
    }

    function getUtilityStakeAddress() external view returns (address) {
        return _utilityStakeAddress;
    }

    function getLiquidityStakeAddress() external view returns (address) {
        return _liquidityStakeAddress;
    }

    function initUtilityStake(address stakeAddress_) external onlyOwner {
        require(_utilityStakeAddress == address(0), "already init");
        _utilityStakeAddress = stakeAddress_;
        _stableCoinToken.transferOwnership(_utilityStakeAddress);
    }

    function initLiquidityStake(address liquidityStake_) external onlyOwner {
        require(_liquidityStakeAddress == address(0), "already init");
        _liquidityStakeAddress = liquidityStake_;
    }

    function initMinter(address minter_) external onlyOwner {
        require(_minter == address(0), "already init");
        _minter = minter_;
        _prToken.transferOwnership(minter_);
        _govToken.transferOwnership(minter_);
    }

    function initVAMM(address vammAddress_) external onlyOwner {
        require(_vammAddress == address(0), "already init");
        _vammAddress = vammAddress_;
        _utilityToken.transferOwnership(_vammAddress);
    }

    function getMinterAddress() external view returns (address) {
        return _minter;
    }

    function getVAMMAddress() external view returns (address) {
        return _vammAddress;
    }

    function getStableCoins() external view returns (address[] memory) {
        return _stableCoins;
    }

    function getCurveStableCoin2CRVPoolAddress()
        external
        view
        returns (address)
    {
        return _curve2CRVXPoolAddress;
    }

    function checkIsOperator(address operator_) external view returns (bool) {
        return _operators[operator_];
    }

    function setOperator(address operator_, bool enabled_) external onlyOwner {
        _operators[operator_] = enabled_;
    }

    /// upgrade --- ///
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
