pragma solidity ^0.8.12;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./IGameNFT.sol";
import "../config/IConfig.sol";
import "./ERC1155Upgradeable.sol";
import "hardhat/console.sol";

contract GameNFT is
    Initializable,
    ERC1155Upgradeable,
    OwnableUpgradeable,
    IGameNFT,
    UUPSUpgradeable
{
    using Strings for uint256;

    uint8 public constant GameNFTPrefix = 0x10;
    uint8 public constant ERC20NFTPrefix = 0x20;
    uint8 public constant ERC721NFTPrefix = 0x30;
    uint8 public constant ERC1155NFTPrefix = 0x40;

    IConfig public config;

    modifier onlyOperator() {
        require(
            config.checkIsOperator(msg.sender),
            "GameNFT: caller is not the operator"
        );
        _;
    }

    function initialize(IConfig config_) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        _transferOwnership(tx.origin);
        config = config_;
    }

    function numberFromAscII(bytes1 b) private pure returns (uint8 res) {
        return uint8(b);
    }

    function toBytes(uint256 x) private pure returns (bytes memory b) {
        uint a = Math.log256(x) + 1;
        b = new bytes(a);
        for (uint i = 0; i < a; i++) {
            b[i] = bytes1(uint8(x / (2 ** (8 * (a - 1 - i)))));
        }
    }

    function hex2Uint256(bytes memory b) public pure returns (uint256 value) {
        uint256 number = numberFromAscII(b[0]);
        if (b.length == 1) {
            return number;
        }
        for (uint i = 1; i < b.length; i++) {
            number <<= 8;
            number += numberFromAscII(b[i]);
        }
        return number;
    }

    function calcERC20TokenId(
        address tokenAddress
    ) public pure returns (uint256) {
        bytes memory tokenAddressBytes = abi.encodePacked(
            uint8(0x20),
            tokenAddress
        );
        return hex2Uint256(tokenAddressBytes);
    }

    function batchMintByERC20(
        address to,
        address[] memory tokenAddresss,
        uint256[] memory amounts,
        bytes memory data
    ) external {
        uint256[] memory tokenIds = new uint256[](tokenAddresss.length);

        for (uint256 i = 0; i < tokenAddresss.length; i++) {
            address tokenAddress = tokenAddresss[i];
            uint256 tokenId = calcERC20TokenId(tokenAddress);

            require(
                IERC20Metadata(tokenAddress).transferFrom(
                    msg.sender,
                    address(this),
                    amounts[i]
                ),
                "GameNFT: transferFrom failed"
            );

            tokenIds[i] = tokenId;
        }
        _mintBatch(msg.sender, to, tokenIds, amounts, data);
    }

    function _itemId(uint256 tokenId) internal pure returns (uint8, uint256) {
        bytes memory tokenBytes = toBytes(tokenId);
        uint256 length = tokenBytes.length;
        if (uint8(tokenBytes[0]) == GameNFTPrefix) {
            if (length < 18) {
                // {0x10}{itemId}{quality}
                bytes memory buffer = new bytes(length - 2);
                for (uint256 i = 0; i < length - 1; i++) {
                    if (i != length - 2) {
                        buffer[i] = tokenBytes[i + 1];
                    }
                }
                return (GameNFTPrefix, hex2Uint256(buffer));
            } else {
                // {0x10}{hash}{itemId}{quality}
                bytes memory buffer = new bytes(length - 18);
                for (uint256 i = 0; i < length - 17; i++) {
                    if (i + 17 != length - 1) {
                        buffer[i] = tokenBytes[i + 17];
                    }
                }
                return (GameNFTPrefix, hex2Uint256(buffer));
            }
        } else if (uint8(tokenBytes[0]) == ERC20NFTPrefix) {
            // {0x20}{address}
            bytes memory buffer = new bytes(length - 1);
            for (uint256 i = 0; i < length - 1; i++) {
                buffer[i] = tokenBytes[i + 1];
            }
            return (ERC20NFTPrefix, hex2Uint256(buffer));
        }

        return (0, 0);
    }

    /// tokenId =
    /// {hash}{itemId}{quality}
    function getItemId(uint256 tokenId) external pure returns (uint8, uint256) {
        return _itemId(tokenId);
    }

    function safeBatchBridge2PublicChainFrom(
        address from,
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts,
        bytes calldata data
    ) external onlyOperator {
        for (uint256 i = 0; i < ids.length; i++) {
            (uint8 prefix, ) = _itemId(ids[i]);
            require(prefix == GameNFTPrefix, "GameNFT: invalid token id");
        }
        _mintBatch(from, to, ids, amounts, data);
        emit Bridge2PublicChain(msg.sender, from, to, ids, amounts);
    }

    function safeBatchBridge2GameFrom(
        address from,
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts
    ) external {
        _burnBatch(from, ids, amounts);
        uint256 gameNFTLength = 0;
        for (uint256 i = 0; i < ids.length; i++) {
            (uint8 prefix, uint256 itemId) = _itemId(ids[i]);
            if (prefix == GameNFTPrefix) {
                gameNFTLength++;
            } else if (prefix == ERC20NFTPrefix) {
                require(
                    IERC20Metadata(address(uint160(itemId))).transfer(
                        to,
                        amounts[i]
                    ),
                    "GameNFT: transferFrom failed"
                );
            } else {
                revert("GameNFT: invalid token id");
            }
        }

        uint256[] memory gameNFTTokenIds = new uint256[](gameNFTLength);
        uint256[] memory gameNFTAmounts = new uint256[](gameNFTLength);
        uint256 index = 0;

        for (uint256 i = 0; i < ids.length; i++) {
            (uint8 prefix, ) = _itemId(ids[i]);
            if (prefix == GameNFTPrefix) {
                gameNFTTokenIds[index] = ids[i];
                gameNFTAmounts[index] = amounts[i];
                index++;
            }
        }

        emit Bridge2GameChain(
            msg.sender,
            from,
            to,
            gameNFTTokenIds,
            gameNFTAmounts
        );
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC1155Upgradeable) returns (bool) {
        return
            interfaceId == type(IGameNFT).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
