pragma solidity ^0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "../config/IConfig.sol";
import "../game/IGameNFT.sol";
import "./IFeeRouter.sol";

contract GewardValut is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    uint128 public constant PRICE_PRECISION = 1e18;

    uint128 public constant PRECENT_DENOMINATOR = 10000000000;

    IConfig public config;

    function initialize() public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        _transferOwnership(tx.origin);
    }

    modifier onlyOperator() {
        bool isOperator = config.checkIsOperator(msg.sender);
        require(msg.sender == owner() || isOperator, "access error");
        _;
    }

    function setConfig(IConfig config_) external onlyOwner {
        config = config_;
    }

    function _tokens() internal view returns (address[] memory) {
        IFeeRouter feeRouter = config.getFeeRouter();
        return feeRouter.getTokens();
    }

    function _collectFees() internal {
        IFeeRouter feeRouter = config.getFeeRouter();
        feeRouter.colletFees();
    }

    function balanceOf() external returns (uint256[] memory) {
        _collectFees();
        address[] memory tokenList = _tokens();
        uint256[] memory balances = new uint256[](tokenList.length);
        for (uint256 i = 0; i < tokenList.length; i++) {
            balances[i] = IERC20Metadata(tokenList[i]).balanceOf(address(this));
        }
        return balances;
    }

    // _createRaffle
    //
    // string memory _raffleName,
    // uint256 _raffleStart,
    // uint256 _raffleEnd,
    // uint256 _ticketId
    // uint256 _minTicket
    // uint256[] _oneTicketPrizeCounts
    bytes4 internal constant _CREATE_RAFFLE_SIG =
        bytes4(
            keccak256(
                "_createRaffle(string,uint256,uint256,uint256,uint256,uint256[])"
            )
        );

    function startRaffle(
        address raffle,
        address gameNFT,
        string memory _raffleName,
        uint256 _raffleStart,
        uint256 _raffleEnd,
        uint256 _ticketId,
        uint256 _minTicket,
        address[] memory _tokenList,
        uint256[] memory _amounts,
        uint256[] memory _oneTicketPrizeCounts
    ) external onlyOperator {
        require(_tokenList.length == _amounts.length, "tokenList length error");
        require(
            _tokenList.length == _oneTicketPrizeCounts.length,
            "tokenList length error"
        );
        _collectFees();
        bytes memory callData = abi.encodePacked(
            _CREATE_RAFFLE_SIG,
            abi.encode(
                _raffleName,
                _raffleStart,
                _raffleEnd,
                _ticketId,
                _minTicket,
                _oneTicketPrizeCounts
            )
        );

        for (uint256 i = 0; i < _tokenList.length; i++) {
            IERC20Metadata(_tokenList[i]).approve(gameNFT, _amounts[i]);
        }

        IGameNFT(gameNFT).batchMintByERC20(
            raffle,
            _tokenList,
            _amounts,
            callData
        );
    }

    /// upgrade --- ///
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
