pragma solidity ^0.8.12;

import "@openzeppelin/contracts/token/ERC1155/extensions/IERC1155MetadataURI.sol";

interface IGameNFT {
    event Bridge2GameChain(
        address indexed operator,
        address indexed from,
        address indexed to,
        uint256[] tokenIds,
        uint256[] values
    );

    event Bridge2PublicChain(
        address indexed operator,
        address indexed from,
        address indexed to,
        uint256[] tokenIds,
        uint256[] values
    );

    function getItemId(uint256 tokenId) external view returns (uint8, uint256);

    function safeBatchBridge2PublicChainFrom(
        address from,
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts,
        bytes calldata data
    ) external;

    function batchMintByERC20(
        address to,
        address[] memory tokenAddresss,
        uint256[] memory amounts,
        bytes memory data
    ) external;

    function safeBatchBridge2GameFrom(
        address from,
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts
    ) external;
}
