pragma solidity ^0.8.12;

import "./IUtilityToken.sol";
import "./PriceField.sol";

interface IVAMM {
    event Mint(
        address indexed buyer,
        uint256 tokenAmount,
        uint256 payAmount,
        uint256 fees
    );
    event Burn(
        address indexed seller,
        uint256 tokenAmount,
        uint256 payAmount,
        uint256 fees
    );
    event UpdateMarketPrice(uint256 newMarketPrice);
    event UpdateMFR(uint256 t, uint256 x);

    function borrow(uint256 _borrowAmount) external;

    function repay(uint256 _repayAmount) external;

    function canBorrowAmount() external view returns (uint256);

    function balanceOf(address token) external view returns (uint256);

    function getPriceField() external view returns (PriceField);

    function mintByPRToken(
        address payTokenAddress,
        uint256 mintAmount,
        uint256 maxPayAmount
    ) external;

    function getLiquidity()
        external
        view
        returns (
            uint256 total,
            uint256 floorPriceSupport,
            uint256 marketSupport,
            uint256 mfr
        );

    function mintByPRToken(
        address payTokenAddress,
        uint256 mintAmount,
        uint256 maxPayAmount,
        address recipient
    ) external;

    function mint(
        address payToken,
        uint256 mintAmount,
        uint256 maxPayAmount
    ) external;

    function mint(
        address payToken,
        uint256 mintAmount,
        uint256 maxPayAmount,
        address recipient
    ) external;

    function burn(
        uint256 brunAmount,
        address receivedToken,
        uint256 minReceivedAmount
    ) external;

    function burn(
        uint256 brunAmount,
        address receivedToken,
        uint256 minReceivedAmount,
        address recipient
    ) external;
}
