pragma solidity ^0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./IVAMM.sol";
import "./IUtilityToken.sol";
import "./config/IConfig.sol";
import "./curve/IPool.sol";
import "./curve/ICurveMetaPoolGauge.sol";
import "./LiquidityStakeHook.sol";

contract LiquidityStake is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    uint128 public constant PRICE_PRECISION = 1e18;
    uint128 public constant PRECENT_DENOMINATOR = 10000000000;
    uint256 public constant MAX_REWARDS = 8;

    IConfig public config;
    LiquidityStakeHook public hook;

    struct StakeInfo {
        uint256 loaned2CRVAmount;
        uint256 totalSC2CRVLP;
    }

    mapping(address => StakeInfo) public stakeInfoByStaker;
    mapping(address => mapping(address => uint256)) public claimDataByStaker;
    mapping(address => mapping(address => uint256)) public rewardIntegralFor;
    mapping(address => uint256) public rewardBalances;
    mapping(address => uint256) public rewardIntegral;

    uint256 public totalLoaned2CRVAmount;
    uint256 public totalBurnSCAmount;

    event Stake(address indexed staker, uint256 amount);
    event UnStake(
        address indexed staker,
        address recipient,
        uint256 lpamount,
        uint256 burnamount,
        uint256 repayamount
    );
    event Claim(address indexed staker, address token, uint256 amount);

    function initialize(IConfig config_) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        _transferOwnership(tx.origin);
        config = config_;
    }

    function setHook(LiquidityStakeHook hook_) external onlyOwner {
        hook = hook_;
    }

    function _sc2crvPool() internal view returns (ICurveMetaPool) {
        address sc2crvPoolAddress = config.getCurveStableCoin2CRVPoolAddress();
        return ICurveMetaPool(sc2crvPoolAddress);
    }

    function _totalSC2CRVLP() internal view returns (uint256) {
        return _sc2crvPool().balanceOf(address(this));
    }

    function _metaGauge() internal view returns (ICurveMetaPoolGauge) {
        return ICurveMetaPoolGauge(_sc2crvPool().rewards_receiver());
    }

    function calcUnstakeToUserAmount(
        address staker,
        uint256 unstakeLP
    ) external view returns (uint256) {
        StakeInfo memory stakeInfo = stakeInfoByStaker[staker];
        if (stakeInfo.totalSC2CRVLP == 0) {
            return 0;
        }
        if (unstakeLP > stakeInfo.totalSC2CRVLP) {
            unstakeLP = stakeInfo.totalSC2CRVLP;
        }
        uint256 repayAmount = Math.mulDiv(
            stakeInfo.loaned2CRVAmount,
            unstakeLP,
            stakeInfo.totalSC2CRVLP
        );
        uint256 t = _totalSC2CRVLP();
        uint256 b0 = _sc2crvPool().balances(0);
        uint256 b1 = _sc2crvPool().balances(1);
        uint256 w0 = Math.mulDiv(unstakeLP, b0, t);
        uint256 w1 = Math.mulDiv(unstakeLP, b1, t);

        if (w1 > repayAmount) {
            w1 = w1 - repayAmount;
        } else {
            w0 = w0 - (repayAmount - w1);
            w1 = 0;
        }

        if (w1 > 0) {
            w0 = w0 + _sc2crvPool().get_dy(1, 0, w1);
        }

        return w0;
    }

    function _borrow2CRV(uint256 _amount) internal returns (uint256) {
        IVAMM vamm = IVAMM(config.getVAMMAddress());
        uint256 borrowAmount = _amount;
        if (vamm.canBorrowAmount() < _amount) {
            borrowAmount = vamm.canBorrowAmount();
        }
        vamm.borrow(borrowAmount);
        totalLoaned2CRVAmount += borrowAmount;
        return borrowAmount;
    }

    function _repay2CRV(uint256 _amount) internal {
        IVAMM vamm = IVAMM(config.getVAMMAddress());
        address tokenAddress = _sc2crvPool().coins(1);
        IERC20Metadata token = IERC20Metadata(tokenAddress);
        token.approve(address(vamm), _amount);
        vamm.repay(_amount);
        totalLoaned2CRVAmount -= _amount;
    }

    function calcStakeLP(uint256 _amount) external view returns (uint256) {
        IVAMM vamm = IVAMM(config.getVAMMAddress());
        uint256 borrowAmount = _amount;
        if (vamm.canBorrowAmount() < _amount) {
            borrowAmount = vamm.canBorrowAmount();
        }
        uint256[2] memory amounts = [_amount, borrowAmount];
        return _sc2crvPool().calc_token_amount(amounts, true);
    }

    function withdrawFees() external {
        _metaGauge().claim_rewards();
        // withdraw CRV
        address crvToken = address(0x11cDb42B0EB46D95f990BeDD4695A6e3fA034978);
        uint256 crvBalance = IERC20Metadata(crvToken).balanceOf(address(this));
        IERC20Metadata(crvToken).transfer(
            address(config.getFeeRouter()),
            crvBalance
        );
    }

    function stake(uint256 _amount, uint256 minMintAmount) external {
        address staker = msg.sender;
        IUtilityToken stablecoin = config.getStableCoinToken();
        _checkpoint(staker);
        stablecoin.transferFrom(staker, address(this), _amount);
        StakeInfo storage stakeInfo = stakeInfoByStaker[staker];
        uint256 borrowed2CRV = _borrow2CRV(_amount);
        stakeInfo.loaned2CRVAmount += borrowed2CRV;
        address tokenAddress = _sc2crvPool().coins(1);
        IERC20Metadata token2crv = IERC20Metadata(tokenAddress);
        // approve
        stablecoin.approve(address(_sc2crvPool()), _amount);
        token2crv.approve(address(_sc2crvPool()), borrowed2CRV);
        // add liqulity
        uint256[2] memory amounts = [_amount, borrowed2CRV];
        uint256 mintLPAmount = _sc2crvPool().add_liquidity(
            amounts,
            minMintAmount
        );
        stakeInfo.totalSC2CRVLP += mintLPAmount;

        if (address(hook) != address(0)) {
            hook.onLiquidityStake(staker, _amount, mintLPAmount);
        }
        // address staker,
        // uint256 amount,
        // uint256 stakerLP
        emit Stake(staker, _amount);
    }

    function unstake(uint256 _unstakeAmount, uint256 min2userAmount) external {
        address staker = msg.sender;
        _unstake(staker, staker, _unstakeAmount, min2userAmount);
    }

    function unstake(
        uint256 _unstakeAmount,
        uint256 min2userAmount,
        address recipient
    ) external {
        address staker = msg.sender;
        _unstake(staker, recipient, _unstakeAmount, min2userAmount);
    }

    function _unstake(
        address staker,
        address recipient,
        uint256 _unstakeAmount,
        uint256 min2userAmount
    ) internal {
        StakeInfo storage stakeInfo = stakeInfoByStaker[staker];
        IERC20Metadata token0 = IERC20Metadata(_sc2crvPool().coins(0));
        IERC20Metadata token1 = IERC20Metadata(_sc2crvPool().coins(1));
        require(stakeInfo.totalSC2CRVLP >= _unstakeAmount, "un01");
        uint256 repayAmount = Math.mulDiv(
            stakeInfo.loaned2CRVAmount,
            _unstakeAmount,
            stakeInfo.totalSC2CRVLP
        );

        _checkpoint(staker);

        uint256 b0 = token0.balanceOf(address(this));
        uint256 b1 = token1.balanceOf(address(this));

        // It doesn't matter the slippage of the curve here, just make sure that the end user receives a sufficient amount
        uint256[2] memory minAmounts = [b1, b1];
        minAmounts[0] = 0;
        minAmounts[1] = 0;
        _sc2crvPool().approve(address(_sc2crvPool()), _unstakeAmount);
        _sc2crvPool().remove_liquidity(_unstakeAmount, minAmounts);

        uint256 w0 = token0.balanceOf(address(this)) - b0;
        uint256 w1 = token1.balanceOf(address(this)) - b1;

        stakeInfo.totalSC2CRVLP -= _unstakeAmount;
        stakeInfo.loaned2CRVAmount -= repayAmount;

        uint256 burnAmount = 0;

        if (w1 > repayAmount) {
            _repay2CRV(repayAmount);
            w1 = w1 - repayAmount;
        } else {
            _repay2CRV(w1);
            burnAmount = (repayAmount - w1);
            totalBurnSCAmount = totalBurnSCAmount + burnAmount;
            w0 = w0 - burnAmount;
            repayAmount = w1;
            w1 = 0;
        }

        if (w1 > 0) {
            w0 = w0 + _sc2crvPool().exchange(1, 0, w1, 0);
        }

        require(w0 >= min2userAmount, "un02");
        token0.transfer(recipient, w0);

        if (address(hook) != address(0)) {
            hook.onLiquidityUnstake(staker, w0, _unstakeAmount);
        }

        // address indexed staker,
        // address recipient,
        // uint256 lpamount,
        // uint256 burnamount,
        // uint256 repayamount
        emit UnStake(
            staker,
            recipient,
            _unstakeAmount,
            burnAmount,
            repayAmount
        );
    }

    function _checkpoint(address staker) internal {
        _metaGauge().claim_rewards();
        StakeInfo memory stakeInfo = stakeInfoByStaker[staker];
        uint256 totalSupplay = _sc2crvPool().totalSupply();
        if (totalSupplay == 0) {
            return;
        }
        for (uint256 i = 0; i < MAX_REWARDS; i++) {
            address tokenAddress = _metaGauge().reward_tokens(i);
            if (tokenAddress == address(0)) {
                break;
            }
            uint256 dI = 0;
            uint256 tokenBalance = IERC20Metadata(tokenAddress).balanceOf(
                address(this)
            );

            dI =
                (10 ** 18 * (tokenBalance - rewardBalances[tokenAddress])) /
                totalSupplay;
            rewardBalances[tokenAddress] = tokenBalance;

            // integral: uint256 = self.reward_integral[token] + dI
            uint256 integral = rewardIntegral[tokenAddress] + dI;
            if (dI != 0) {
                rewardIntegral[tokenAddress] = integral;
            }

            uint256 integralFor = rewardIntegralFor[tokenAddress][staker];
            uint256 newClaimable = 0;

            if (integralFor < integral) {
                rewardIntegralFor[tokenAddress][staker] = integral;
                newClaimable =
                    (stakeInfo.totalSC2CRVLP * ((integral - integralFor))) /
                    PRICE_PRECISION;
            }

            uint256 claimData = claimDataByStaker[staker][tokenAddress];
            uint256 totalClaimable = (claimData >> 128) + newClaimable;

            if (totalClaimable > 0) {
                uint256 totalClaimed = claimData % 2 ** 128;
                if (newClaimable > 0) {
                    claimDataByStaker[staker][tokenAddress] =
                        totalClaimed +
                        (totalClaimable << 128);
                }
            }
        }
    }

    function viewCanClaimAmount(
        address staker,
        address token
    ) external returns (uint256) {
        _checkpoint(staker);
        uint256 claimData = claimDataByStaker[staker][token];
        return claimData >> 128;
    }

    function claim(address token) external {
        address staker = msg.sender;
        _checkpoint(staker);

        uint256 claimData = claimDataByStaker[staker][token];
        claimDataByStaker[staker][token] = 0;

        uint256 totalClaimble = claimData >> 128;
        rewardBalances[token] -= totalClaimble;

        IERC20Metadata(token).transfer(staker, totalClaimble);

        emit Claim(staker, token, totalClaimble);
    }

    /// upgrade --- ///
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
