interface IMarketplace is IERC1155ReceiverUpgradeable {
    struct Order {
        uint256 id;
        address seller;
        uint256 tokenId;
        uint256 amount;
        address nft;
        uint256 priceInWeiForUnit;
    }

    struct AutoBuyFixStack2deep {
        uint256 poolBuyAmount;
        uint256 poolBuyPrice;
        uint256 totalPrice;
        uint256 index;
    }

    struct AutoBuyFixStack2deepReturns {
        uint256[] execOrderIds;
        uint256[] execOrderAmounts;
        uint256 poolBuyAmount;
        uint256 poolBuyPrice;
        uint256 needTotalPrice;
    }

    event OrderCreated(uint256 id, bytes32 productId, Order order);

    event OrderCanceled(uint256 id);

    event OrderUpdated(uint256 id, uint256 newPriceInWeiForUnit);

    event OrderSellout(
        uint256 id,
        uint256 amount,
        bool isSellout,
        address payment,
        address recipient,
        uint256 paymentTotal,
        uint256 fees
    );

    event ChangedOwnerCutPerMillion(uint256 ownerCutPerMillion);
}
