pragma solidity ^0.8.12;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155ReceiverUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "./IMarketplace.sol";
import "./IGameNFT.sol";
import "../config/IConfig.sol";
import "../feerouter/IWithdrawFees.sol";

contract Marketplace is
    Initializable,
    IMarketplace,
    UUPSUpgradeable,
    OwnableUpgradeable,
    ERC1155ReceiverUpgradeable,
    IWithdrawFees
{
    bytes4 internal constant ERC1155_RECEIVED_VALUE = 0xf23a6e61;
    bytes4 internal constant ERC1155_BATCH_RECEIVED_VALUE = 0xbc197c81;

    uint128 public constant PRICE_PRECISION = 1e18;
    uint128 public constant PRECENT_DENOMINATOR = 10000000000;

    using AddressUpgradeable for address;
    using SafeMathUpgradeable for uint256;
    using CountersUpgradeable for CountersUpgradeable.Counter;

    CountersUpgradeable.Counter private _orderIdCounter;

    IERC20 public acceptedToken;

    uint256 public ownerCutPerMillion;

    IConfig public config;

    mapping(uint256 => Order) public orderById;

    mapping(bytes32 => uint256[]) private _orderIdsByProductId;

    function initialize(IConfig _config) public initializer {
        __UUPSUpgradeable_init();
        __Ownable_init();
        config = _config;
        acceptedToken = IERC20(address(config.getStableCoinToken()));
        _setOwnerCutPerMillion(300000000);
    }

    // 300000000 = 3%
    // 3000000000 = 30%
    // 10000000000
    function setOwnerCutPerMillion(
        uint256 _ownerCutPerMillion
    ) public onlyOwner {
        _setOwnerCutPerMillion(_ownerCutPerMillion);
    }

    function getDefaultProductId(
        address nft,
        uint256 tokenId
    ) internal view returns (bytes32) {
        IERC165Upgradeable nftDetect = IERC165Upgradeable(nft);
        if (nftDetect.supportsInterface(type(IGameNFT).interfaceId)) {
            (, uint256 itemId) = IGameNFT(nft).getItemId(tokenId);
            bytes32 productId = keccak256(abi.encodePacked(itemId));
            return productId;
        }
        revert("Marketplace#getDefaultProductId: Invalid NFT");
    }

    function withdrawFees() external override {
        address feerouter = address(config.getFeeRouter());
        uint256 amount = acceptedToken.balanceOf(address(this));
        acceptedToken.transfer(feerouter, amount);
    }

    function _addOrderIdsByProductIdAndERC20Address(
        bytes32 productId,
        uint256 orderId
    ) private {
        if (_orderIdsByProductId[productId].length == 0) {
            _orderIdsByProductId[productId] = [orderId];
        } else {
            _orderIdsByProductId[productId].push(orderId);
        }
    }

    function _removeOrderIdsByProductIdAndERC20Address(
        bytes32 productId,
        uint256 orderId
    ) private {
        uint256 index;
        uint256 length = _orderIdsByProductId[productId].length;
        for (uint256 i = 0; i < length; i++) {
            if (_orderIdsByProductId[productId][i] == orderId) {
                index = i;
                break;
            }
        }
        require(
            _orderIdsByProductId[productId][index] == orderId,
            "Marketplace#_removeOrderIdsByProductId:  orderId not found"
        );
        uint256 lastOrderId = _orderIdsByProductId[productId][length - 1];

        _orderIdsByProductId[productId][index] = lastOrderId;

        _orderIdsByProductId[productId].pop();
    }

    function _setOwnerCutPerMillion(uint256 _ownerCutPerMillion) internal {
        require(
            _ownerCutPerMillion < PRECENT_DENOMINATOR,
            "Marketplace#_setOwnerCutPerMillion: The owner cut should be between 0 and 10000000000"
        );

        ownerCutPerMillion = _ownerCutPerMillion;
        emit ChangedOwnerCutPerMillion(ownerCutPerMillion);
    }

    function _erc721validate(
        IERC721Upgradeable nft,
        uint256 tokenId,
        uint256 amount,
        address sender
    ) internal view {
        require(
            nft.ownerOf(tokenId) == sender,
            "Marketplace#erc721validate: The sender is not the owner of the token"
        );

        require(
            nft.getApproved(tokenId) == address(this) ||
                nft.isApprovedForAll(sender, address(this)),
            "Marketplace#erc721validate: The contract is not authorized to manage the asset"
        );

        require(
            amount == 1,
            "Marketplace#erc721validate: The amount should be 1"
        );
    }

    function _erc1155validate(
        IERC1155Upgradeable nft,
        uint256 tokenId,
        uint256 amount,
        uint256 erc1155recivedAmount,
        address sender
    ) internal view {
        if (amount != erc1155recivedAmount) {
            require(
                nft.balanceOf(sender, tokenId) > 0,
                "Marketplace#_erc1155validate: Only the owner can create orders"
            );
            require(
                nft.isApprovedForAll(sender, address(this)),
                "Marketplace#_erc1155validate: The contract is not authorized to manage the asset"
            );
        }
    }

    function executeOrder(
        uint256 orderId,
        address recipient,
        uint256 amount,
        uint256 priceInWeiForUnit
    ) external {
        address payment = _msgSender();
        _executeOrder(orderId, payment, recipient, amount, priceInWeiForUnit);
    }

    function updateOrder(uint256 orderId, uint256 priceInWeiForUnit) external {
        address operator = _msgSender();
        _updateOrder(orderId, priceInWeiForUnit, operator);
    }

    function _updateOrder(
        uint256 orderId,
        uint256 priceInWeiForUnit,
        address operator
    ) internal {
        Order memory order = orderById[orderId];
        require(
            order.seller == operator,
            "Marketplace#_updateOrder: The operator is not the seller"
        );

        order.priceInWeiForUnit = priceInWeiForUnit;
        orderById[orderId] = order;

        emit OrderUpdated(orderId, priceInWeiForUnit);
    }

    function _min(uint256 a, uint256 b) private pure returns (uint256) {
        if (a < b) {
            return a;
        } else {
            return b;
        }
    }

    function _removeArrayIndex(
        uint256[] memory elements,
        uint256 index
    ) private pure returns (uint256[] memory) {
        uint256[] memory newElements = new uint256[](elements.length - 1);
        for (uint256 i = 0; i < newElements.length; i++) {
            if (i != index && i < index) {
                newElements[i] = elements[i];
            } else {
                newElements[i] = elements[i + 1];
            }
        }
        return newElements;
    }

    function autoBuy(
        bytes32 productId,
        address recipient,
        uint256 amount,
        uint256 maxPayableAmount
    ) external {
        AutoBuyFixStack2deepReturns memory ret = autoBuyPrice(
            productId,
            amount
        );
        address payment = _msgSender();
        require(
            ret.needTotalPrice <= maxPayableAmount,
            "Marketplace#autoBuy: The total price is not correct"
        );

        uint256 allowance = acceptedToken.allowance(payment, address(this));
        require(
            allowance >= maxPayableAmount,
            "Marketplace#autoBuy: The allowance is not enough"
        );

        for (uint256 i = 0; i < ret.execOrderIds.length; i++) {
            uint256 orderId = ret.execOrderIds[i];
            if (orderId > 0) {
                uint256 orderAmount = ret.execOrderAmounts[i];
                uint256 priceInWeiForUnit = orderById[orderId]
                    .priceInWeiForUnit;
                _executeOrder(
                    orderId,
                    payment,
                    recipient,
                    orderAmount,
                    priceInWeiForUnit
                );
            }
        }
    }

    function getProductLowestPrice(
        bytes32 productId
    ) external view returns (uint256) {
        uint256[] memory orderIds = _orderIdsByProductId[productId];

        if (orderIds.length == 0) {
            return 0;
        }

        if (orderIds.length == 1) {
            return orderById[orderIds[0]].priceInWeiForUnit;
        }

        uint256 lowestPrice = orderById[orderIds[0]].priceInWeiForUnit;
        for (uint256 i = 1; i < orderIds.length; i++) {
            uint256 orderId = orderIds[i];
            if (orderId > 0) {
                uint256 priceInWeiForUnit = orderById[orderId]
                    .priceInWeiForUnit;
                lowestPrice = _min(lowestPrice, priceInWeiForUnit);
            }
        }

        return lowestPrice;
    }

    function _comparePrice(
        Order memory order,
        uint256 poolPrice,
        uint256 poolAmount
    ) internal view returns (bool) {
        if (poolAmount == 0) {
            return true;
        }
        uint256 priceInWeiForUnit = order.priceInWeiForUnit;
        uint256 fees = (priceInWeiForUnit * ownerCutPerMillion) / 1000000;
        priceInWeiForUnit += fees;
        uint256 poolUnitPrice = poolPrice / poolAmount;

        if (priceInWeiForUnit < poolUnitPrice) {
            return true;
        }

        return false;
    }

    // Help users find the cheapest orders
    function autoBuyPrice(
        bytes32 productId,
        uint256 neededAmount
    ) public view returns (AutoBuyFixStack2deepReturns memory) {
        uint256[] memory orderIds = _orderIdsByProductId[productId];
        // uint256 poolStock = factory.getStock(
        //     productId,
        //     IERC20Metadata(erc20token)
        // );
        AutoBuyFixStack2deep memory params = AutoBuyFixStack2deep(0, 0, 0, 0);

        AutoBuyFixStack2deepReturns memory ret = AutoBuyFixStack2deepReturns(
            new uint256[](orderIds.length),
            new uint256[](orderIds.length),
            params.poolBuyAmount,
            params.poolBuyPrice,
            params.poolBuyPrice
        );

        if (orderIds.length == 0) {
            if (params.poolBuyAmount < neededAmount) {
                ret.poolBuyAmount = 0;
                ret.poolBuyPrice = 0;
                ret.needTotalPrice = 0;
                return ret;
            }
            ret.poolBuyAmount = params.poolBuyAmount;
            ret.poolBuyPrice = params.poolBuyPrice;
            ret.needTotalPrice = params.poolBuyPrice + params.totalPrice;
            return ret;
        }

        while (neededAmount > 0 && orderIds.length > 0) {
            Order memory order = orderById[orderIds[0]];
            uint256 matchOrderIndex = 0;

            for (uint256 i = 0; i < orderIds.length; i++) {
                Order memory newOrder = orderById[orderIds[i]];
                if (newOrder.amount == 0) {
                    continue;
                }
                if (newOrder.priceInWeiForUnit > 0) {
                    if (newOrder.priceInWeiForUnit < order.priceInWeiForUnit) {
                        order = newOrder;
                        matchOrderIndex = i;
                    }
                }
            }

            // 除非池子数量不足, 否则如果最低的订单价格比池子价格高
            if (
                !_comparePrice(
                    order,
                    params.poolBuyPrice,
                    params.poolBuyAmount
                ) && params.poolBuyAmount >= neededAmount
            ) {
                ret.poolBuyAmount = params.poolBuyAmount;
                ret.poolBuyPrice = params.poolBuyPrice;
                ret.needTotalPrice = params.poolBuyPrice + params.totalPrice;
                return ret;
            }

            uint256 amount = _min(neededAmount, order.amount);

            if (neededAmount == params.poolBuyAmount) {
                if (params.poolBuyAmount >= amount) {
                    params.poolBuyAmount -= amount;
                } else {
                    params.poolBuyAmount = 0;
                }
            }

            neededAmount -= amount;

            params.totalPrice =
                params.totalPrice +
                order.priceInWeiForUnit *
                amount;
            params.poolBuyPrice = 0;
            ret.execOrderIds[params.index] = order.id;
            ret.execOrderAmounts[params.index] = amount;
            // Remove the order from the list
            orderIds = _removeArrayIndex(orderIds, matchOrderIndex);
            params.index++;
        }

        ret.poolBuyAmount = params.poolBuyAmount;
        ret.poolBuyPrice = params.poolBuyPrice;
        ret.needTotalPrice = params.poolBuyPrice + params.totalPrice;
        return ret;
    }

    // cancel order
    function cancelOrder(uint256 orderId) external {
        address operator = _msgSender();
        Order memory order = orderById[orderId];
        require(
            order.seller == operator,
            "Marketplace#cancelOrder: The operator is not the seller"
        );
        require(
            order.amount > 0,
            "Marketplace#cancelOrder: The order is already executed"
        );
        address nft = order.nft;
        uint256 tokenId = order.tokenId;
        bytes32 productId = getDefaultProductId(nft, tokenId);
        _nftTransfer(nft, tokenId, address(this), operator, order.amount);
        order.amount = 0;
        orderById[orderId] = order;
        _removeOrderIdsByProductIdAndERC20Address(productId, orderId);
        emit OrderCanceled(orderId);
    }

    function _nftTransfer(
        address nft,
        uint256 tokenId,
        address from,
        address to,
        uint256 amount
    ) private {
        ERC165Upgradeable nftDetect = ERC165Upgradeable(nft);

        if (nftDetect.supportsInterface(type(IGameNFT).interfaceId)) {
            uint256[] memory tokenIds = new uint256[](1);
            tokenIds[0] = tokenId;
            uint256[] memory amounts = new uint256[](1);
            amounts[0] = amount;
            IGameNFT(nft).safeBatchBridge2GameFrom(from, to, tokenIds, amounts);
            return;
        }

        if (nftDetect.supportsInterface(type(IERC721Upgradeable).interfaceId)) {
            IERC721Upgradeable(nft).safeTransferFrom(from, to, tokenId);
        } else if (
            nftDetect.supportsInterface(type(IERC1155Upgradeable).interfaceId)
        ) {
            IERC1155Upgradeable(nft).safeTransferFrom(
                from,
                to,
                tokenId,
                amount,
                ""
            );
        } else {
            revert("Marketplace#_executeOrder: nft must be erc721 or erc1155");
        }
    }

    function _executeOrder(
        uint256 orderId,
        address payment,
        address recipient,
        uint256 amount,
        uint256 priceInWeiForUnit
    ) internal {
        Order memory order = orderById[orderId];
        require(
            order.amount > 0,
            "Marketplace#_executeOrder: The order amount should be greater than 0"
        );
        require(
            order.priceInWeiForUnit <= priceInWeiForUnit,
            "Marketplace#_executeOrder: The order price should be less than the current price"
        );
        require(
            order.amount >= amount,
            "Marketplace#_executeOrder: Insufficient remaining quantity "
        );

        uint256 allowance = acceptedToken.allowance(payment, address(this));
        uint256 totalPrice = amount * priceInWeiForUnit;
        require(
            allowance >= totalPrice,
            "Marketplace#_executeOrder: Insufficient allowance"
        );

        order.amount -= amount;
        orderById[orderId] = order;

        uint256 saleShareAmount = 0;
        if (ownerCutPerMillion > 0) {
            // Calculate sale share
            saleShareAmount = totalPrice.mul(ownerCutPerMillion).div(
                PRECENT_DENOMINATOR
            );
            acceptedToken.transferFrom(payment, address(this), saleShareAmount);
        }

        require(
            acceptedToken.transferFrom(
                payment,
                order.seller,
                totalPrice.sub(saleShareAmount)
            ),
            "Marketplace#_executeOrder: Transfering the sale amount to the seller failed"
        );

        address nft = order.nft;
        uint256 tokenId = order.tokenId;

        _nftTransfer(nft, tokenId, address(this), recipient, amount);

        bytes32 productId = getDefaultProductId(nft, tokenId);

        if (order.amount == 0) {
            _removeOrderIdsByProductIdAndERC20Address(productId, order.id);
        }

        emit OrderSellout(
            orderId,
            amount,
            order.amount == 0,
            payment,
            recipient,
            totalPrice,
            saleShareAmount
        );
    }

    function _createOrder(
        address nft,
        address sender,
        uint256 tokenId,
        uint256 amount,
        uint256 erc1155recivedAmount,
        uint256 priceInWeiForUnit
    ) internal {
        _orderIdCounter.increment();
        require(nft.isContract(), "The nft is not a contract");
        require(priceInWeiForUnit > 0, "Price should be bigger than 0");
        ERC165Upgradeable nftDetect = ERC165Upgradeable(nft);

        if (nftDetect.supportsInterface(type(IERC721Upgradeable).interfaceId)) {
            _erc721validate(IERC721Upgradeable(nft), tokenId, amount, sender);
        } else if (
            nftDetect.supportsInterface(type(IERC1155Upgradeable).interfaceId)
        ) {
            _erc1155validate(
                IERC1155Upgradeable(nft),
                tokenId,
                amount,
                erc1155recivedAmount,
                sender
            );
        } else {
            revert("Marketplace#_createOrder: nft must be erc721 or erc1155");
        }

        bytes32 productId = getDefaultProductId(nft, tokenId);
        uint256 orderId = _orderIdCounter.current();

        Order memory order = Order(
            orderId,
            sender,
            tokenId,
            amount,
            nft,
            priceInWeiForUnit
        );

        orderById[orderId] = order;

        _addOrderIdsByProductIdAndERC20Address(productId, orderId);

        emit OrderCreated(orderId, productId, order);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(ERC1155ReceiverUpgradeable, IERC165Upgradeable)
        returns (bool)
    {
        return
            interfaceId == type(ERC1155ReceiverUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    bytes4 internal constant CREATE_ORDER_SIG =
        bytes4(keccak256("_createOrder(uint256)"));

    function onERC1155BatchReceived(
        address, // _operator,
        address _from,
        uint256[] memory _ids,
        uint256[] memory _amounts,
        bytes calldata _data
    ) public override returns (bytes4) {
        // This function assumes that the ERC-1155 token contract can
        // only call `onERC1155BatchReceived()` via a valid token transfer.
        // Users must be responsible and only use this Niftyswap exchange
        // contract with ERC-1155 compliant token contracts.
        // Obtain method to call via object signature
        bytes4 functionSignature = bytes4(_data[:4]);
        require(
            functionSignature == CREATE_ORDER_SIG,
            "Marketplace#onERC1155BatchReceived5: Invalid function signature"
        );
        uint256 priceInWeiForUnit;
        (priceInWeiForUnit) = abi.decode(_data[4:], (uint256));
        for (uint256 i = 0; i < _ids.length; i++) {
            _createOrder(
                _msgSender(),
                _from,
                _ids[i],
                _amounts[i],
                _amounts[i],
                priceInWeiForUnit
            );
        }
        return ERC1155_BATCH_RECEIVED_VALUE;
    }

    /**
     * @dev Will pass to onERC115Batch5Received
     */
    function onERC1155Received(
        address _operator,
        address _from,
        uint256 _id,
        uint256 _amount,
        bytes calldata _data
    ) public override returns (bytes4) {
        uint256[] memory ids = new uint256[](1);
        uint256[] memory amounts = new uint256[](1);

        ids[0] = _id;
        amounts[0] = _amount;

        require(
            ERC1155_BATCH_RECEIVED_VALUE ==
                onERC1155BatchReceived(_operator, _from, ids, amounts, _data),
            "Marketplace#onERC1155Received: INVALID_ONRECEIVED_MESSAGE"
        );

        return ERC1155_RECEIVED_VALUE;
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
