pragma solidity ^0.8.12;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

struct Raffle {
    RaffleItem raffleItem;
    uint256 acceptedTicketId;
    // maps what tickets entrants have entered into the raffle
    // entrant => tickets
    mapping(address => Entry[]) entries;
    // the addresses of people who have entered tickets into the raffle
    address[] entrants;
    address creator;
    bool closed;
    // TODO
    // vrf randomness
    uint256 randomNumber;
    bool randomNumberPending;
    // date in timestamp seconds when a raffle start
    uint256 raffleStart;
    // date in timestamp seconds when a raffle ends
    uint256 raffleEnd;
}

// The minimum rangeStart is 0
// The maximum rangeEnd is raffleItem.totalEntered
// rangeEnd - rangeStart == number of ticket entered for raffle item by a entrant entry
struct Entry {
    bool prizesClaimed;
    uint256 rangeStart; // Raffle number. Value is between 0 and raffleItem.totalEntered - 1
    uint256 rangeEnd; // Raffle number. Value is between 1 and raffleItem.totalEntered
}

struct RaffleItemPrize {
    uint256 prizeQuantityRemaining; // Remaining number of erc1155 tokens
    uint256 prizeQuantity; // Number of ERC1155 tokens
    uint256 oneTicketPrizeCount;
    uint256 prizeTokenId; // ERC1155 token type
}

// Ticket numbers are numbers between 0 and raffleItem.totalEntered - 1 inclusive.
struct RaffleItem {
    uint256 totalEntered; // Total number of ERC1155 tokens entered into raffle for this raffle item
    uint256 minTicket;
    RaffleItemPrize[] raffleItemPrizes; // Prizes that can be won for this raffle item
} 

interface IRaffles {
    struct RaffleItemInput {
        uint256 ticketId;
        uint256 minTicket;
        RaffleItemPrizeIO[] raffleItemPrizes;
    }
    struct RaffleItemPrizeIO {
        uint256 prizeTokenId;
        uint256 oneTicketPrizeCount;
        uint256 prizeQuantity;
    }
    struct EntryIO {
        uint256 ticketId; // ERC1155 type id
        uint256 ticketQuantity; // Number of ERC1155 tokens
        uint256 rangeStart;
        uint256 rangeEnd;
        uint256 raffleItemIndex;
        bool prizesClaimed;
    }
    struct RaffleIO {
        uint256 raffleId;
        uint256 raffleEnd;
        bool isOpen;
    }
    struct TicketItemIO {
        uint256 ticketId; // ERC1155 type id
        uint256 ticketQuantity; // Number of ERC1155 tokens
    }
    struct RaffleItemOutput {
        uint256 totalEntered;
        RaffleItemPrizeIO[] raffleItemPrizes;
    }
    struct PrizesWinIO {
        uint256 raffleItemPrizeIndex; // index into the raffleItemPrizes array (which prize was won)
        uint256[] winningPrizeNumbers; // ticket numbers between 0 and raffleItem.totalEntered that won
    }
    struct TicketWinIO {
        uint256 entryIndex; // index into a user's array of tickets (which staking attempt won)
        PrizesWinIO[] prizes;
    }
    event RaffleCreated(
        uint256 raffleId,
        string raffleName,
        uint256 raffleStart,
        uint256 raffleEnd,
        address creator,
        RaffleItemInput raffleItem
    );
    event RaffleTicketsEntered(
        uint256 raffleId,
        address entrant,
        TicketItemIO ticketItem,
        Entry entry,
        uint256 entryIndex
    );
    event RaffleRandomNumber(uint256 raffleId, uint256 randomNumber);
    event RaffleClaimPrize(
        uint256 raffleId,
        address entrant,
        uint256 prizeTokenId,
        uint256 prizeQuantity
    );
    event RafflePrizeWithdraw(uint256 raffleId);
}
