pragma solidity ^0.8.12;

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/interfaces/IERC165.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155ReceiverUpgradeable.sol";
import "./IRaffles.sol";
import "./IGameNFT.sol";

contract Raffles is
    IRaffles,
    Initializable,
    UUPSUpgradeable,
    OwnableUpgradeable,
    ERC1155ReceiverUpgradeable
{
    Raffle[] public raffles;

    address public gameNFT;

    bytes4 internal constant ERC1155_RECEIVED_VALUE = 0xf23a6e61;
    bytes4 internal constant ERC1155_BATCH_RECEIVED_VALUE = 0xbc197c81;

    bytes4 internal constant ERC1155_ACCEPTED = 0xf23a6e61; // Return value from `onERC1155Received` call if a contract accepts receipt (i.e `bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"))`).

    function initialize(address _gameNFT) public initializer {
        __UUPSUpgradeable_init();
        __Ownable_init();

        // skip raffle 0
        raffles.push();
        gameNFT = _gameNFT;

        _transferOwnership(tx.origin);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    function rawFulfillRandomness(uint256 raffleId) external {
        uint256 _randomnes = block.timestamp;
        Raffle storage raffle = raffles[raffleId];

        require(raffle.raffleItem.minTicket <= raffle.raffleItem.totalEntered, "Raffle: Not enough tickets sold");
        require(raffle.raffleEnd < block.timestamp, "Raffle: Raffle time has not expired");
        require(
            raffle.randomNumber == 0,
            "Raffle: Random number already generated"
        );
        raffles[raffleId].randomNumber = _randomnes;
        raffle.randomNumberPending = false;
        emit RaffleRandomNumber(raffleId, _randomnes);
    }

    function _createRaffle(
        address _creator,
        string memory _raffleName,
        uint256 _raffleStart,
        uint256 _raffleEnd,
        RaffleItemInput memory _raffleItem
    ) internal {
        require(
            _raffleStart - block.timestamp >= 0,
            "Raffle: _raffleStartDuration must be greater than 1 mintes"
        );
        require(
            _raffleEnd > block.timestamp,
            "Raffle: _raffleEndDuration must be greater than 1 hour"
        );
        // require(
        //     _raffleEnd - block.timestamp >= 3600,
        //     "Raffle: _raffleEndDuration must be greater than 1 hour"
        // );
        require(
            _raffleItem.raffleItemPrizes.length > 0,
            "Raffle: No raffle items"
        );
        uint256 raffleId = raffles.length;
        emit RaffleCreated(
            raffleId,
            _raffleName,
            _raffleStart,
            _raffleEnd,
            _creator,
            _raffleItem
        );
        Raffle storage raffle = raffles.push();
        raffle.raffleEnd = _raffleEnd;
        raffle.raffleStart = _raffleStart;
        raffle.creator = _creator;
        raffle.closed = false;
        require(_raffleItem.raffleItemPrizes.length > 0, "Raffle: No prizes");
        raffle.acceptedTicketId = _raffleItem.ticketId;
        raffle.raffleItem.minTicket = _raffleItem.minTicket;
        for (uint256 j; j < _raffleItem.raffleItemPrizes.length; j++) {
            RaffleItemPrizeIO memory raffleItemPrizeIO = _raffleItem
                .raffleItemPrizes[j];
            raffle.raffleItem.raffleItemPrizes.push(
                RaffleItemPrize(
                    raffleItemPrizeIO.prizeQuantity,
                    raffleItemPrizeIO.prizeQuantity,
                    raffleItemPrizeIO.oneTicketPrizeCount,
                    raffleItemPrizeIO.prizeTokenId
                )
            );
        }
    }

    // _enterTickets
    // uint256 _raffleId, TicketItemIO memory ticketItem
    // _createRaffle
    //
    // string memory _raffleName,
    // uint256 _raffleStart,
    // uint256 _raffleEnd,
    // uint256 _ticketId
    // uint256[] _oneTicketPrizeCounts
    bytes4 internal constant _CREATE_RAFFLE_SIG =
        bytes4(
            keccak256(
                "_createRaffle(string,uint256,uint256,uint256,uint256,uint256[])"
            )
        );
    bytes4 internal constant _ENTER_TICKETS_SIG =
        bytes4(keccak256("_enterTickets(uint256)"));

    struct CreateRaffleParams {
        string raffleName;
        uint256 raffleStart;
        uint256 raffleEnd;
        uint256 ticketId;
        uint256 minTicket;
        uint256[] oneTicketPrizeCounts;
    }

    function onERC1155BatchReceived(
        address, // _operator,
        address _from,
        uint256[] memory _ids,
        uint256[] memory _amounts,
        bytes calldata _data
    ) public override returns (bytes4) {
        require(
            msg.sender == address(gameNFT),
            "Raffles: onERC1155BatchReceived only gameNFT address"
        );
        bytes4 functionSignature = bytes4(_data[:4]);

        if (functionSignature == _CREATE_RAFFLE_SIG) {
            CreateRaffleParams memory _params;
            RaffleItemInput memory _raffleItem;
            (
                _params.raffleName,
                _params.raffleStart,
                _params.raffleEnd,
                _params.ticketId,
                _params.minTicket,
                _params.oneTicketPrizeCounts
            ) = abi.decode(
                _data[4:],
                (string, uint256, uint256, uint256, uint256, uint256[])
            );
            RaffleItemPrizeIO[] memory prizes = new RaffleItemPrizeIO[](
                _ids.length
            );
            for (uint256 i = 0; i < _ids.length; i++) {
                prizes[i] = RaffleItemPrizeIO(
                    _ids[i],
                    _params.oneTicketPrizeCounts[i],
                    _amounts[i] / _params.oneTicketPrizeCounts[i]
                );
            }
            _raffleItem.minTicket = _params.minTicket;
            _raffleItem.ticketId = _params.ticketId;
            _raffleItem.raffleItemPrizes = prizes;
            _createRaffle(
                _from,
                _params.raffleName,
                _params.raffleStart,
                _params.raffleEnd,
                _raffleItem
            );
        } else if (functionSignature == _ENTER_TICKETS_SIG) {
            require(_ids.length == 1, "Raffle: enter ticket only one");
            uint256 _raffleId;
            TicketItemIO memory ticketItem = TicketItemIO(_ids[0], _amounts[0]);
            (_raffleId) = abi.decode(_data[4:], (uint256));
            _enterTickets(_from, _raffleId, ticketItem);
        } else {
            revert("Raffles: INVALID_METHOD");
        }

        return ERC1155_BATCH_RECEIVED_VALUE;
    }

    /**
     * @dev Will pass to onERC115Batch5Received
     */
    function onERC1155Received(
        address _operator,
        address _from,
        uint256 _id,
        uint256 _amount,
        bytes calldata _data
    ) public override returns (bytes4) {
        uint256[] memory ids = new uint256[](1);
        uint256[] memory amounts = new uint256[](1);

        ids[0] = _id;
        amounts[0] = _amount;

        require(
            ERC1155_BATCH_RECEIVED_VALUE ==
                onERC1155BatchReceived(_operator, _from, ids, amounts, _data),
            "Meland.Marketplace#onERC1155Received: INVALID_ONRECEIVED_MESSAGE"
        );

        return ERC1155_RECEIVED_VALUE;
    }

    /**
     * @notice Get total number of raffles that exist.
     */
    function raffleSupply() external view returns (uint256 raffleSupply_) {
        raffleSupply_ = raffles.length;
    }

    function _enterTickets(
        address from,
        uint256 _raffleId,
        TicketItemIO memory ticketItem
    ) internal {
        require(_raffleId < raffles.length, "Raffle: Raffle does not exist");
        require(
            ticketItem.ticketQuantity > 0,
            "Raffle: Ticket quantity cannot be zero"
        );
        Raffle storage raffle = raffles[_raffleId];
        require(raffle.acceptedTicketId != 0, "Raffle: Raffle does not exist");
        require(
            raffle.raffleStart < block.timestamp,
            "Raffle: Raffle time no start"
        );
        require(
            raffle.raffleEnd > block.timestamp,
            "Raffle: Raffle time has expired"
        );
        require(
            raffle.acceptedTicketId == ticketItem.ticketId,
            "Raffle: Raffle acceptedTicketId no eq"
        );
        // Collect unique entrant addresses
        if (raffle.entries[from].length == 0) {
            raffle.entrants.push(from);
        }
        uint256 totalEntered = raffle.raffleItem.totalEntered;
        Entry memory entry = Entry(
            false,
            totalEntered,
            totalEntered + ticketItem.ticketQuantity
        );

        uint256 entryIndex = raffle.entries[from].length;
        // Create a range of unique numbers for ticket ids
        raffle.entries[from].push(entry);
        emit RaffleTicketsEntered(
            _raffleId,
            from,
            ticketItem,
            entry,
            entryIndex
        );
        // update the total quantity of tickets that have been entered for this raffle item
        raffle.raffleItem.totalEntered =
            totalEntered +
            ticketItem.ticketQuantity;
    }

    function withdrawPrize(uint256 _raffleId) external {
        require(_raffleId < raffles.length, "Raffle: Raffle does not exist");
        Raffle storage raffle = raffles[_raffleId];
        require(
            raffle.creator == _msgSender(),
            "Raffle: only creator withdraw"
        );
        require(raffle.closed == false, "Raffle: raffle is closed");
        raffle.closed = true;
        for (
            uint256 i = 0;
            i < raffle.raffleItem.raffleItemPrizes.length;
            i++
        ) {
            RaffleItemPrize storage raffleItemPrize = raffle
                .raffleItem
                .raffleItemPrizes[i];
            if (raffleItemPrize.prizeQuantityRemaining > 0) {
                uint256[] memory tokenIds = new uint256[](1);
                tokenIds[0] = raffleItemPrize.prizeTokenId;
                uint256[] memory amounts = new uint256[](1);
                amounts[0] = raffleItemPrize.prizeQuantityRemaining;
                IGameNFT(gameNFT).safeBatchBridge2GameFrom(
                    address(this),
                    raffle.creator,
                    tokenIds,
                    amounts
                );
                raffleItemPrize.prizeQuantityRemaining = 0;
            }
        }
        emit RafflePrizeWithdraw(_raffleId);
    }

    // Get the unique addresses of entrants in a raffle
    function getEntrants(
        uint256 _raffleId
    ) external view returns (address[] memory entrants_) {
        require(_raffleId < raffles.length, "Raffle: Raffle does not exist");
        Raffle storage raffle = raffles[_raffleId];
        entrants_ = raffle.entrants;
    }

    function _getTotalPrizeNumber(
        uint256 prizeQuantity,
        uint256 raffleItemPrizeIndex,
        uint256 prizeNumber
    ) internal pure returns (uint256) {
        if (raffleItemPrizeIndex == 0) {
            return prizeNumber;
        }
        return prizeQuantity + prizeNumber;
    }

    function claimPrize(
        uint256 _raffleId,
        address _entrant,
        TicketWinIO[] calldata _wins
    ) external {
        require(_raffleId < raffles.length, "Raffle: Raffle does not exist");
        Raffle storage raffle = raffles[_raffleId];
        uint256 randomNumber = raffle.randomNumber;
        require(raffle.closed == false, "Raffle: raffle is closed");
        require(randomNumber > 0, "Raffle: Random number not generated yet");
        require(_wins.length > 0, "Raffle: Wins is empty");
        // contractOwner can claim prizes for the entrant.  Prizes are only transferred to the entrant
        require(
            msg.sender == _entrant,
            "Raffle: Not claimed by owner or contractOwner"
        );
        uint256 lastValue = raffle.entries[_entrant].length;
        for (uint256 i; i < _wins.length; i++) {
            TicketWinIO calldata win = _wins[i];
            // Serves two purposes: 1. Ensure is less than raffle.entries[_entrant].length. 2. prevents duplicates
            require(
                win.entryIndex < lastValue,
                "Raffle: User entry does not exist or is not lesser than last value"
            );
            Entry memory entry = raffle.entries[_entrant][win.entryIndex];
            require(
                entry.prizesClaimed == false,
                "Raffles: Entry prizes have already been claimed"
            );
            raffle.entries[_entrant][win.entryIndex].prizesClaimed = true;
            uint256 totalEntered = raffle.raffleItem.totalEntered;
            lastValue = raffle.raffleItem.raffleItemPrizes.length;
            for (uint256 j; j < win.prizes.length; j++) {
                PrizesWinIO calldata prize = win.prizes[j];
                // Serves two purposes: 1. Ensure is less than raffleItemPrizes.length. 2. prevents duplicates
                require(
                    prize.raffleItemPrizeIndex < lastValue,
                    "Raffle: Raffle prize type does not exist or is not lesser than last value"
                );
                RaffleItemPrize storage raffleItemPrize = raffle
                    .raffleItem
                    .raffleItemPrizes[prize.raffleItemPrizeIndex];
                lastValue = raffleItemPrize.prizeQuantity;
                for (uint256 k; k < prize.winningPrizeNumbers.length; k++) {
                    uint256 prizeNumber = prize.winningPrizeNumbers[k];
                    // Serves two purposes: 1. Ensure is less than raffleItemPrize.prizeQuantity. 2. prevents duplicates
                    require(
                        prizeNumber < lastValue,
                        "Raffle: prizeNumber does not exist or is not lesser than last value"
                    );
                    uint256 winningTicketNumber = uint256(
                        keccak256(
                            abi.encodePacked(
                                randomNumber,
                                prize.raffleItemPrizeIndex,
                                prizeNumber
                            )
                        )
                    ) % totalEntered;
                    require(
                        winningTicketNumber >= entry.rangeStart &&
                            winningTicketNumber < entry.rangeEnd,
                        "Raffle: Did not win prize"
                    );
                    lastValue = prizeNumber;
                }
                emit RaffleClaimPrize(
                    _raffleId,
                    _entrant,
                    raffleItemPrize.prizeTokenId,
                    prize.winningPrizeNumbers.length *
                        raffleItemPrize.oneTicketPrizeCount
                );
                require(
                    raffleItemPrize.prizeQuantityRemaining >=
                        prize.winningPrizeNumbers.length,
                    "Raffle: prize remainging not enough"
                );
                raffleItemPrize.prizeQuantityRemaining =
                    raffleItemPrize.prizeQuantityRemaining -
                    prize.winningPrizeNumbers.length;

                uint256[] memory tokenIds = new uint256[](1);
                tokenIds[0] = raffleItemPrize.prizeTokenId;
                uint256[] memory amounts = new uint256[](1);
                amounts[0] =
                    prize.winningPrizeNumbers.length *
                    raffleItemPrize.oneTicketPrizeCount;
                IGameNFT(gameNFT).safeBatchBridge2GameFrom(
                    address(this),
                    msg.sender,
                    tokenIds,
                    amounts
                );
                lastValue = prize.raffleItemPrizeIndex;
            }
            lastValue = win.entryIndex;
        }
    }
}
