pragma solidity ^0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./IVAMM.sol";
import "./IUtilityToken.sol";
import "./config/IConfig.sol";
import "./PriceField.sol";
import "./UtilityStakeHook.sol";

contract UtilityStake is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    uint128 public constant PRICE_PRECISION = 1e18;

    uint128 public constant PRECENT_DENOMINATOR = 10000000000;

    IConfig public config;

    uint256 public rate;
    uint256 public periodFinish;

    uint256 public timeoutClaimPeriod;
    uint256 public periodDuration;
    uint256 public unstakeFee;

    struct StakeInfo {
        uint256 stakedAmount;
        uint256 loanedAmount;
        uint256 claimedAmount;
        uint256 latestClaimedAt;
    }

    mapping(address => StakeInfo) public stakeInfoByStaker;

    uint256 public totalStakeAmount;

    UtilityStakeHook public hook;

    event Lend(address indexed staker, uint256 amount);
    event Claim(address indexed staker, uint256 amount);
    event Repay(address indexed staker, uint256 amount);
    event Stake(address indexed staker, uint256 amount);
    event UnStake(address indexed staker, uint256 amount);
    event DepositRewardToken(uint256 amount);

    function initialize(IConfig config_) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        _transferOwnership(tx.origin);
        config = config_;

        unstakeFee = 300000000;
        periodDuration = 1 weeks;
        timeoutClaimPeriod = 2 days;
    }

    function setHook(UtilityStakeHook hook_) external onlyOwner {
        hook = hook_;
    }

    function calcLendAmount(uint256 _amount) external view returns (uint256) {
        IVAMM vamm = IVAMM(config.getVAMMAddress());
        PriceField priceField = vamm.getPriceField();
        uint256 floorPrice = priceField.floorPrice();
        return
            Math.mulDiv(
                _amount,
                floorPrice,
                PRICE_PRECISION,
                Math.Rounding.Zero
            );
    }

    function lendPrice() external view returns (uint256) {
        return IVAMM(config.getVAMMAddress()).getPriceField().floorPrice();
    }

    function _calcCanLendAmount(address staker) private view returns (uint256) {
        StakeInfo memory stakeInfo = stakeInfoByStaker[staker];
        if (stakeInfo.stakedAmount == 0) {
            return 0;
        }

        IVAMM vamm = IVAMM(config.getVAMMAddress());
        PriceField priceField = vamm.getPriceField();
        uint256 floorPrice = priceField.floorPrice();

        uint256 totalLendAmount = Math.mulDiv(
            stakeInfo.stakedAmount,
            floorPrice,
            PRICE_PRECISION,
            Math.Rounding.Zero
        );

        if (totalLendAmount < stakeInfo.loanedAmount) {
            return 0;
        }

        return totalLendAmount - stakeInfo.loanedAmount;
    }

    function _lend(address staker, uint256 amount) private {
        StakeInfo storage stakeInfo = stakeInfoByStaker[staker];
        stakeInfo.loanedAmount += amount;
        IUtilityToken stablecoin = config.getStableCoinToken();
        stablecoin.mint(staker, amount);
        emit Lend(staker, amount);
    }

    function userInfo(
        address staker
    )
        external
        view
        returns (
            uint256 stakedAmount,
            uint256 loanedAmount,
            uint256 canLendAmount,
            uint256 canClaimAmount
        )
    {
        StakeInfo memory stakeInfo = stakeInfoByStaker[staker];
        stakedAmount = stakeInfo.stakedAmount;
        loanedAmount = stakeInfo.loanedAmount;
        canLendAmount = _calcCanLendAmount(staker);
        canClaimAmount = _calcClaimableAmount(staker);
    }

    function stake(uint256 amount) external {
        IUtilityToken utilityToken = config.getUtilityToken();
        address staker = msg.sender;
        utilityToken.transferFrom(staker, address(this), amount);
        StakeInfo storage stakeInfo = stakeInfoByStaker[staker];
        _claim(staker, stakeInfo);
        stakeInfo.stakedAmount += amount;
        totalStakeAmount += amount;
        uint256 canLendAmount = _calcCanLendAmount(staker);
        if (canLendAmount > 0) {
            _lend(staker, canLendAmount);
        }
        require(
            utilityToken.balanceOf(address(this)) >= totalStakeAmount,
            "UtilityStake: balanceOf < totalStakeAmount"
        );

        if (address(hook) != address(0)) {
            hook.onUtilityStake(
                staker, 
                amount
            );
        }

        emit Stake(staker, amount);
    }

    function calcUnstakeRepayAmount(
        address staker,
        uint256 unstakeAmount
    ) public view returns (uint256 repayAmount, uint256 fees) {
        StakeInfo memory stakeInfo = stakeInfoByStaker[staker];
        if (stakeInfo.loanedAmount > 0) {
            repayAmount = Math.min(
                Math.mulDiv(
                    stakeInfo.loanedAmount,
                    unstakeAmount,
                    stakeInfo.stakedAmount,
                    Math.Rounding.Up
                ),
                stakeInfo.loanedAmount
            );
            fees = Math.mulDiv(repayAmount, unstakeFee, PRECENT_DENOMINATOR);
        }
    }

    function _repay(
        address staker,
        StakeInfo storage stakeInfo,
        uint256 unstakeAmount
    ) private {
        (uint256 repayAmount, uint256 fees) = calcUnstakeRepayAmount(
            staker,
            unstakeAmount
        );
        stakeInfo.loanedAmount -= repayAmount;
        IUtilityToken stablecoin = config.getStableCoinToken();
        stablecoin.burnFrom(staker, repayAmount);

        // fees
        stablecoin.transferFrom(staker, address(this), fees);

        emit Repay(staker, repayAmount);
    }

    function unstake(uint256 unstakeAmount) external {
        address staker = msg.sender;
        StakeInfo storage stakeInfo = stakeInfoByStaker[staker];

        _repay(staker, stakeInfo, unstakeAmount);
        _claim(staker, stakeInfo);

        stakeInfo.stakedAmount -= unstakeAmount;
        totalStakeAmount -= unstakeAmount;

        IUtilityToken utilityToken = config.getUtilityToken();
        utilityToken.transfer(staker, unstakeAmount);

        require(
            utilityToken.balanceOf(address(this)) >= totalStakeAmount,
            "UtilityStake: balanceOf < totalStakeAmount+totalUnstakeAmount"
        );

        if (address(hook) != address(0)) {
            hook.onUtilityUnstake(
                staker, 
                unstakeAmount
            );
        }

        emit UnStake(staker, unstakeAmount);
    }

    function _calcClaimableAmount(
        address staker
    ) internal view returns (uint256) {
        StakeInfo memory stakeInfo = stakeInfoByStaker[staker];
        if (stakeInfo.stakedAmount == 0) {
            return 0;
        }
        if (stakeInfo.latestClaimedAt == 0) {
            return 0;
        }
        if (stakeInfo.latestClaimedAt > block.timestamp) {
            return 0;
        }
        if (totalStakeAmount == 0) {
            return 0;
        }
        if (rate == 0) {
            return 0;
        }
        uint256 diff = block.timestamp - stakeInfo.latestClaimedAt;
        uint256 claimD = diff % timeoutClaimPeriod;

        // Avoid delayed on-chain submissions resulting in zero rewards 0
        if (diff == timeoutClaimPeriod) {
            claimD = timeoutClaimPeriod;
        }

        uint256 claimableAmount = Math.mulDiv(
            stakeInfo.stakedAmount * claimD,
            rate,
            totalStakeAmount,
            Math.Rounding.Zero
        );
        return claimableAmount;
    }

    function _claim(
        address staker,
        StakeInfo storage stakeInfo
    ) internal returns (uint256) {
        uint256 claimableAmount = _calcClaimableAmount(staker);
        stakeInfo.latestClaimedAt = block.timestamp;
        if (claimableAmount > 0) {
            IUtilityToken rewardToken = config.getPRToken();
            rewardToken.transfer(staker, claimableAmount);
            stakeInfo.claimedAmount += claimableAmount;
        }
        emit Claim(staker, claimableAmount);
        return claimableAmount;
    }

    function claim() external returns (uint256) {
        address staker = msg.sender;
        StakeInfo storage stakeInfo = stakeInfoByStaker[staker];
        uint256 claimableAmount = _claim(staker, stakeInfo);
        return claimableAmount;
    }

    function depositRewardToken(uint256 amount) external {
        IUtilityToken rewardToken = config.getPRToken();
        rewardToken.transferFrom(msg.sender, address(this), amount);
        if (block.timestamp >= periodFinish) {
            rate = amount / periodDuration;
        } else {
            uint256 remaining = periodFinish - block.timestamp;
            uint256 leftover = remaining * rate;
            rate = (amount + leftover) / periodDuration;
        }
        periodFinish = block.timestamp + periodDuration;
        emit DepositRewardToken(amount);
    }

    function withdrawFees() external {
        IUtilityToken stablecoin = config.getStableCoinToken();
        stablecoin.transfer(
            address(config.getFeeRouter()),
            stablecoin.balanceOf(address(this))
        );
    }

    /// upgrade --- ///
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}
